mapper.select:
SELECT board_no AS no, board_title AS title, board_content AS content, board_writer AS writer, board_writer_id AS writer_id, DATE_FORMAT(board_date, "%Y년 %c월 %e일 %r") AS date
FROM board
ORDER BY board_no DESC;

SELECT board_no AS no, board_title AS title, board_content AS content, board_writer AS writer, board_writer_id AS writer_id, DATE_FORMAT(board_date, "%Y년 %c월 %e일 %r") AS date
FROM board
WHERE board_no = #{no};

mapper.insert:
INSERT INTO board
(board_title, board_content, board_writer, board_writer_id) VALUES
(#{title}, #{content}, #{writer}, #{writer_id});

mapper.update:
UPDATE board 
SET board_title = #{title}, board_content = #{content} 
WHERE board_no = #{no}
